import React from 'react'

const RECORD_OPTIONS = {
  video: true,
  audio: true,
}

const MESSAGE_HEADER = "You are recording your screen"
const MESSAGE_CONTENT = "Use the controls below to pause, publish or discard recording"


export default class RecordingService {
  /**
   * static ref for video node
   */
  static videoRef = document.getElementById("video")
  static msgContainer = document.getElementById("message")
  static div = document.createElement('div')


  /**
   * function to record screen
   */
  static startRecording = async (callback) => {
    try {
      this.videoRef.style.display='block'
      this.videoRef.setAttribute('autoplay',true)
      this.videoRef.srcObject = await navigator.mediaDevices.getDisplayMedia(RECORD_OPTIONS)
      return true
    } catch (err) {
      console.error("Error: " + err)
      return false
    }
  }

  /**
   * function to stop recording
   */
  static stopRecording = () => {
    try {
      let tracks = this.videoRef.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      this.videoRef.srcObject = null;
      this.removeMessage()
    } catch (error) {
      console.error("error: ",error) 
    }
  }

  static pauseRecording = () => {
    try {
      let tracks = this.videoRef.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      this.hideVideo()
      this.showMessage()
    } catch (error) {
      console.error("error: ",error)
    }
  }

  static hideVideo = () => {
    this.videoRef.style.display="none"
  }

  static showMessage = () => {
    try {
      const hasChild = this.msgContainer.children.length > 0
      const messageHead = document.createElement('p')
      const messageInfo = document. createElement('p')
      this.div.className='msg-content'
      messageHead.innerText = MESSAGE_HEADER
      messageInfo.innerHTML = MESSAGE_CONTENT
      this.div.appendChild(messageHead)
      this.div.appendChild(messageInfo)
      if(hasChild){
        return
      }else{
        this.msgContainer.appendChild(this.div)
      }
    } catch (error) {
      console.error("error: ",error)
    }
  }

  static removeMessage = () => {
    this.msgContainer.style.display='none'
  }
}
