import React, { Component, useRef, useState } from "react"
import { Image, StyleSheet, Text, View } from "react-native"
import Button from "../components/Button"
import RecordingService from '../services/RecordingService';


// const videoRef = document.getElementById("video")
const Record = () => {
  const [recording, setRecording] = useState(false)
  const [paused, setPaused] = useState(false)

  const onRecord = async () => {
    setRecording(true)
    await RecordingService.startRecording()
    setRecording(false)
  }

  const onStopRecord = () => {
    setRecording(false)
    RecordingService.stopRecording()
  }

  const onPause = () => {
    setPaused(true)
    RecordingService.pauseRecording()
  }

  const RecordButton = (props) => {
    const Icon = <View style={[styles.recordIcon]} />
    return <Button {...props}>{Icon}</Button>
  }

  const StopButton = (props) => {
    const Icon = (
      <View style={[styles.recordIcon]}>
        <View style={[styles.square]} />
      </View>
    )
    return <Button {...props}>{Icon}</Button>
  }

  const UploadButton = (props) => {
    return (
      <Button {...props}>
        <Image style={[styles.icon]} source={require("../assets/image.png")} />
      </Button>
    )
  }

  const RecordScreenButton = (props) => {
    return (
      <Button {...props}>
        <Image
          style={[styles.icon]}
          source={require("../assets/monitor.png")}
        />
      </Button>
    )
  }

  const renderPausedBtns = () => {
    return (
      <>
        <StopButton onPress={onStopRecord} />
      </>
    )
  }

  const renderDefaultBtns = () => {
    return (
      <>
        <RecordScreenButton onPress={onRecord} />
        <RecordButton onPress={recording?onRecord:onPause} />
        <UploadButton />
      </>
    )
  }

  return (
    <View style={[styles.container]}>
      <View style={[styles.btnRow]}>
        {paused
          ?renderPausedBtns()
          :renderDefaultBtns()}
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    alignItems: "center"
  },
  btnRow: {
    flexDirection: "row",
    marginVertical: 25,
    alignItems: "center"
  },
  recordIcon: {
    height: 50,
    width: 50,
    borderRadius: 100,
    backgroundColor: "red",
    justifyContent: "center",
    alignItems: "center"
  },
  stopIcon: {},
  square: {
    height: 25,
    width: 25,
    backgroundColor: "#fff"
  },
  icon: {
    height: 40,
    width: 40
  }
})

export default Record
