import React, { Component, useRef } from "react"
import { Image, StyleSheet, Text, View } from "react-native"
import Button from "../components/Button"

const RECORD_OPTIONS = {
  video: {
    cursor: "always"
  },
  audio: {
    echoCancellation: true,
    noiseSuppression: true,
    sampleRate: 44100
  }
}

const videoRef = document.getElementById("video")

const Record = () => {
  // const videoRef = useRef()
  const v = document.createElement("video")
  const onRecord = async () => {
    try {
      v.srcObject = await navigator.mediaDevices.getDisplayMedia(RECORD_OPTIONS)
      document.getElementById('video').appendChild(v)
    } catch (err) {
      console.error("Error: " + err)
    }
  }

  // const onStop

  const RecordButton = (props) => {
    const Icon = <View style={[styles.recordIcon]} />
    return <Button {...props}>{Icon}</Button>
  }

  const StopButton = (props) => {
    const Icon = (
      <View style={[styles.recordIcon]}>
        <View style={[styles.square]} />
      </View>
    )
    return <Button {...props}>{Icon}</Button>
  }

  const UploadButton = (props) => {
    return (
      <Button {...props}>
        <Image style={[styles.icon]} source={require("../assets/image.png")} />
      </Button>
    )
  }

  const RecordScreenButton = (props) => {
    return (
      <Button {...props}>
        <Image
          style={[styles.icon]}
          source={require("../assets/monitor.png")}
        />
      </Button>
    )
  }

  // return null
  return (
    <View style={[styles.container]}>
      <View style={[styles.btnRow]}>
        <RecordScreenButton onPress={onRecord} />
        <RecordButton onPress={onRecord} />
        <StopButton />
        <UploadButton />
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center"
  },
  btnRow: {
    flexDirection: "row",
    marginVertical: 25,
    alignItems: "center"
  },
  recordIcon: {
    height: 50,
    width: 50,
    borderRadius: 100,
    backgroundColor: "red",
    justifyContent: "center",
    alignItems: "center"
  },
  stopIcon: {},
  square: {
    height: 25,
    width: 25,
    backgroundColor: "#fff"
  },
  icon: {
    height: 40,
    width: 40
  }
})

export default Record
